INSERT INTO "T_Short_Description" VALUES(107,-213977473,'Egal wie alt - alle können Podcasts produzieren. Hast Du auch schon überlegt, mit dem Podcasten anzufangen? Am Podcasttisch kann man das Podcasten gleich ausprobieren.','2000-02-02 00:00:00');
