INSERT INTO "T_Short_Description" VALUES(217,-1499742147,'Wolfgang Scheffler zeigt uns an praktischen Beispielen, wie wir einen effektiven demokratischen Prozess selbst in die Hand nehmen können und unsere Gesellschaft im Großen wie im kleinen als BürgerInnen selbst gestalten, statt von wenigen passiv gestaltet zu werden.','2000-02-02 00:00:00');
